import React, { useState } from 'react';

const defaultTasks = [
  { text: "Утренняя гигиена", done: true },
  { text: "Приём лекарств", done: false },
  { text: "Прогулка", done: false },
  { text: "Обед", done: false },
  { text: "Вечерняя гигиена", done: false }
];

function Plan() {
  const [tasks, setTasks] = useState(defaultTasks);
  const [input, setInput] = useState("");

  const addTask = () => {
    if (input.trim()) {
      setTasks([...tasks, { text: input, done: false }]);
      setInput("");
    }
  };

  const toggleTask = idx => {
    setTasks(tasks.map((t, i) => i === idx ? { ...t, done: !t.done } : t));
  };

  return (
    <div style={{ padding: '32px 16px 0 16px' }}>
      <h2 style={{ marginBottom: 24 }}>План ухода</h2>
      <div style={{ marginBottom: 18 }}>
        <input
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Добавить задачу..."
          style={{
            padding: '10px 16px',
            borderRadius: 8,
            border: '1px solid var(--border)',
            fontSize: 15,
            marginRight: 8
          }}
        />
        <button
          onClick={addTask}
          style={{
            background: 'var(--button)',
            color: 'var(--button-fg)',
            border: 'none',
            borderRadius: 8,
            padding: '10px 18px',
            fontWeight: 600,
            fontSize: 15,
            cursor: 'pointer'
          }}
        >
          Добавить
        </button>
      </div>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {tasks.map((t, i) => (
          <li
            key={i}
            style={{
              background: 'var(--card)',
              borderRadius: 12,
              marginBottom: 12,
              padding: '14px 18px',
              display: 'flex',
              alignItems: 'center',
              fontSize: 16,
              textDecoration: t.done ? 'line-through' : 'none',
              color: t.done ? 'var(--text-secondary)' : 'var(--text)'
            }}
            onClick={() => toggleTask(i)}
          >
            <input
              type="checkbox"
              checked={t.done}
              onChange={() => toggleTask(i)}
              style={{ marginRight: 12 }}
            />
            {t.text}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Plan;
